package training;

public class EvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 20; i++) {
            // Check if the number is even
            if (i % 2 == 0) {
                System.out.println(i); // Print the even number
            }
        }


	}

}
